from Crypto.Util.number import long_to_bytes, bytes_to_long, getPrime
from secret import flag

p, q = getPrime(512), getPrime(512)
n = p*q
e = 65537
c = pow(bytes_to_long(flag), e, n)
hint = p - q

print(f'{n = }')
print(f'{c = }')
print(f'{hint = }')

